mapper.select:
    select id, area_kbn as areaKbn, area_name as areaName, follow_area_id as followAreaId, 
    del_flg as delFlg, version, create_id as createId, create_time as createTime, 
    update_id as updateId, update_time as updateTime 
    from AREA 
    where del_flg = 0 

		AND id = #{id, jdbcType=BIGINT} 


		AND area_kbn = #{areaKbn, jdbcType=CHAR} 


		AND follow_area_id = #{followAreaId, jdbcType=BIGINT}
